mapper.select:
select id, name, category, price
from Products
where id = #{id}

select id, name, category, price from Products
WHERE (name LIKE CONCAT('%', #{keyword}, '%') OR category LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY id DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE Products
SET name = #{name},
category = #{category},
price = #{price},
stock_manage = #{stockManage}
WHERE id = #{id}
